import Swal from 'sweetalert2'
import { Controller } from "@hotwired/stimulus"

// Connects to data-controller="sweetalert"
export default class extends Controller {
  static values = { title: String,
                    confirmText: String,
                    cancelText: String }

  connect() {
    this.redirect = false;
  }

  procesoSatisfactorio(event){
    if (this.redirect) return;
    event.stopImmediatePropagation();
    event.preventDefault();

    Swal.fire({
        title: 'Excelente',
        html: 'You clicked the button!',
        icon: 'success'
    })
  }

  procesoCancelado(event){
    if (this.redirect) return;
    event.stopImmediatePropagation();
    event.preventDefault();

    Swal.fire({
      title: 'Cancelado',
      html: 'Something went wrong!',
      icon: 'error'
    })
  }

  procesoAlerta(event){
    if (this.redirect) return;
    event.stopImmediatePropagation();
    event.preventDefault();

    Swal.fire({
      title: 'Alerta',
      html: 'Something went wrong!',
      position: 'center',
      icon: 'warning',
      timer: 4000,
      timerProgressBar: true,
      confirmButtonColor: "#1c7a1e"
    })
  }

  showAlert(event){
    if (this.redirect) return;

    event.stopImmediatePropagation();
    event.preventDefault();

    Swal.fire({
      title: 'ALERTA',
      text: this.titleValue,
      icon: 'info',
      backdrop: false,
      background: "white",
      confirmButtonColor: "#1c7a1e",
      timer: 5000,
      timerProgressBar: true,
      toast: false,
      position: 'center',
      allowOutsideClick: false,
      allowEscapeKey: false,
      allowEnterKey: false,
      stopKeydownPropagation: false,
      buttonsStyling: true,
      showCloseButton: true,
      reverseButtons: true,
      focusConfirm: false,
      focusCancel: true,
      //showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: this.confirmTextValue,
      //denyButtonText: `Don't save`,
      cancelButtonText: this.cancelTextValue,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        this.redirect = true;
        this.element.click();
        Swal.fire(
            'Deleted!',
            'Your file has been deleted.',
            'success'
        )
      } else if (result.isDenied) {
        //Swal.fire('Changes are not saved', '', 'info')
      }
    });
  }

  inactivarAlert(event){
    if (this.redirect) return;

    event.stopImmediatePropagation();
    event.preventDefault();

    Swal.fire({
      toast: false,
      position: 'center',
      timer: 5000,
      timerProgressBar: true,
      backdrop: true,
      title: 'Estas seguro?',
      text: this.titleValue,
      icon: 'warning',
      reverseButtons: true,
      showCancelButton: true,
      confirmButtonColor: '#029b4e',
      cancelButtonColor: '#d33',
      confirmButtonText: this.confirmTextValue,
      cancelButtonText: this.cancelTextValue
    }).then((result) => {
      if (result.isConfirmed) {
        this.redirect = true;
        this.element.click();
      }
    })
  }

}
